import React from 'react';
import ReactDOM from 'react-dom';
import { createStore } from 'redux';
import { Provider } from 'react-redux';

// 초기 상태와 액션 타입 정의
const initialState = {
  title: 'Redux Example',
  message: 'Welcome to Redux Example',
};

const CHANGE_TITLE = 'CHANGE_TITLE';
const CHANGE_MESSAGE = 'CHANGE_MESSAGE';

// 액션 생성자 함수 정의
function changeTitle(newTitle) {
  return { type: CHANGE_TITLE, payload: newTitle };
}

function changeMessage(newMessage) {
  return { type: CHANGE_MESSAGE, payload: newMessage };
}

// 리듀서 정의
function reducer(state = initialState, action) {
  switch (action.type) {
    case CHANGE_TITLE:
      return { ...state, title: action.payload };
    case CHANGE_MESSAGE:
      return { ...state, message: action.payload };
    default:
      return state;
  }
}

// 스토어 생성
const store = createStore(reducer);

// 화면 구성 컴포넌트
function App() {
  const { title, message } = store.getState();

  return (
    <div style={{ width: '900px', height: '500px' }}>
      <h1>{title}</h1>
      <p>{message}</p>
      <button onClick={() => store.dispatch(changeTitle('New Title'))}>
        Change Title
      </button>
      <button onClick={() => store.dispatch(changeMessage('New Message'))}>
        Change Message
      </button>
    </div>
  );
}

ReactDOM.render(
  <Provider store={store}>
    <App />
  </Provider>,
  document.getElementById('root')
);

export default Modal;
